import {useState} from 'react';
import ExpensesFilter from './ExpensesFilter';
import Card from '../UI/Card';
import ExpenseItem from "./ExpenseItem";

import './Expenses.css';

const Expenses = (props) => {
    const [chosenYear, setChosenYear] = useState('2022');

    const yearFilterChangeHandler = (chosenYear) => {
        setChosenYear(chosenYear);
    };

    const filteredExpenses = props.items.filter((expense) => {
        return expense.date.getFullYear().toString() === chosenYear;
    });

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter
                   onFilterChange={yearFilterChangeHandler} selected={chosenYear}/>
                <ExpenseItem
                    title={props.item[0].title}
                    amount={props.item[0].amount}
                    date={props.item[0].date}
                />
                <ExpenseItem
                    title={props.item[1].title}
                    amount={props.item[1].amount}
                    date={props.item[1].date}
                />
                <ExpenseItem
                    title={props.item[2].title}
                    amount={props.item[2].amount}
                    date={props.item[2].date}
                />
                <ExpenseItem
                    title={props.item[3].title}
                    amount={props.item[3].amount}
                    date={props.item[3].date}
                />
            </Card>
        </div>

    );

};

export default Expenses;
